mapper.insert:
INSERT INTO BOARD (
	  BID
	, CATECD
	, TITLE
	, CONTENT
	, TAG
	, VIEWCNT
	, REGCD
	, REGDT
	, MODCD
	, MODDT
) VALUES (
	  #{bid}
	, #{catecd}
	, #{title}
	, replace(replace(replace(#{content}, '\r\n', ''), char(10), ''), char(13), '')
	, #{tag}
	, 0
	, #{sid}
	, now()
	, #{sid}
	, now()
)

mapper.update:
UPDATE BOARD SET
	  CATECD = #{catecd}
	, TITLE = #{title}
	, CONTENT = replace(replace(replace(#{content}, '\r\n', ''), char(10), ''), char(13), '')
	, TAG = #{tag}
	, MODCD = #{sid}
	, MODDT = now()
WHERE BID = #{bid}

UPDATE BOARD SET
	VIEWCNT = VIEWCNT + 1
WHERE BID = #{bid}
