mapper.insert:
 
		INSERT INTO USER_INFO (
			   userno
		     , usertype
		     , username
		     , name
		     , password
             , email
             , deptcd
             , poscd
             , tel
             , addr
             , addrdtl
             , useflag
             , langcd
             , regcd
             , regdt	
		) VALUES ( 
			   #{userno}
			  , 'IN'
		      , #{username}
		      , #{name} 
		      , #{password}
		      , #{email}
		      , #{deptcd}
		      , #{poscd}
		      , #{tel}
		      , #{addr} 
		      , #{addrdtl} 
		      , 'Y'
		      , 'KO'
		      , #{sid}
		      , getdate()
		)

mapper.update:
 
		UPDATE USER_INFO SET 
			name = #{name} 
		  , password = #{password} 
		  , email = #{email} 
		WHERE userno = #{userno}
